import{_ as a,c as e,o as t,a1 as r}from"./chunks/framework.B5mX7_2E.js";const l="/docs/assets/analysis-diagram.DvMKcm9S.png",i="/docs/assets/target-audience-analysis.B9gIUBUX.png",f=JSON.parse('{"title":"Шаги создания приложения","description":"","frontmatter":{},"headers":[],"relativePath":"product/steps.md","filePath":"product/steps.md"}'),o={name:"product/steps.md"},s=r('<h1 id="шаги-создания-приложения" tabindex="-1">Шаги создания приложения <a class="header-anchor" href="#шаги-создания-приложения" aria-label="Permalink to &quot;Шаги создания приложения&quot;">​</a></h1><h2 id="анализ-идеи-приложения-анализ-похожих-приложении" tabindex="-1">Анализ идеи приложения. Анализ похожих приложений. <a class="header-anchor" href="#анализ-идеи-приложения-анализ-похожих-приложении" aria-label="Permalink to &quot;Анализ идеи приложения. Анализ похожих приложений.&quot;">​</a></h2><p>На этом этапе нужно получить ответы на вопросы:</p><ol><li>Какую проблему будет решать приложение?</li><li>Зачем приложение нужно мне? Как оно будет приносить прибыль? (если она подразумевается)</li><li>Какие приложения схожей тематики уже существуют?</li></ol><p>Результат - понимание что кому для чего и почему мы делаем, отражен в виде диаграммы.</p><p><img src="'+l+'" alt="analysis-diagram-result"></p><h2 id="анализ-ца-приложения" tabindex="-1">Анализ ЦА приложения. <a class="header-anchor" href="#анализ-ца-приложения" aria-label="Permalink to &quot;Анализ ЦА приложения.&quot;">​</a></h2><p>На этом этапе нужно получить ответы на вопросы:</p><ol><li>Кто целевая аудитория приложения?</li><li>Какие группы пользователей можно выделить из целевой аудитории?</li><li>Для каждой группы пользователей нужно составить Ключевые персоны.</li><li>Для Ключевых персон составляем User story по принципу. &quot;Как &lt;роль пользователя&gt;, я &lt;что-то хочу получить&gt; &lt;с такой-то целью&gt;&quot;</li><li>От User story переходим к составлению User scenario. Это подробный путь пользователя в приложении, который он сделает для достижения своей цели.</li></ol><p>Результата - User scenario, для каких платформ разрабатываем приложение. User scenario должны быть зафиксированы в виде схем. Какие разрешения поддерживаем.</p><p><img src="'+i+'" alt="target-audience-analysis-result"></p><h2 id="построение-гипотез" tabindex="-1">Построение гипотез <a class="header-anchor" href="#построение-гипотез" aria-label="Permalink to &quot;Построение гипотез&quot;">​</a></h2><ol><li>Нужно определить какие цели и в какие сроки преследуем</li><li>Составить роадмап развития проекта</li></ol><h2 id="проектирование-ит-системы" tabindex="-1">Проектирование ИТ системы <a class="header-anchor" href="#проектирование-ит-системы" aria-label="Permalink to &quot;Проектирование ИТ системы&quot;">​</a></h2><ol><li>Примерная схема самой системы</li><li>Первычная оценка объема работ</li></ol><h2 id="прототипирование-интерфеиса-приложения" tabindex="-1">Прототипирование интерфейса приложения <a class="header-anchor" href="#прототипирование-интерфеиса-приложения" aria-label="Permalink to &quot;Прототипирование интерфейса приложения&quot;">​</a></h2><p>Результат - Wireframe. Какие экраны и точки входа в приложение будут. Также на них накладывается User flow.</p><h2 id="стилизация" tabindex="-1">Стилизация. <a class="header-anchor" href="#стилизация" aria-label="Permalink to &quot;Стилизация.&quot;">​</a></h2><p>Результат - несколько экранов, оформленные в фирменном стиле.</p><h2 id="дизаин" tabindex="-1">Дизайн. <a class="header-anchor" href="#дизаин" aria-label="Permalink to &quot;Дизайн.&quot;">​</a></h2><p>Результат - готовые макеты приложения.</p><h2 id="разработка" tabindex="-1">Разработка <a class="header-anchor" href="#разработка" aria-label="Permalink to &quot;Разработка&quot;">​</a></h2><h2 id="тестирование" tabindex="-1">Тестирование <a class="header-anchor" href="#тестирование" aria-label="Permalink to &quot;Тестирование&quot;">​</a></h2><p><a href="https://vc.ru/design/58502-etapy-razrabotki-polzovatelskogo-interfeysa-kak-sdelat-tak-chtoby-ui-ne-lishil-vas-pribyli" target="_blank" rel="noreferrer">Ссылка про этапы</a></p><p><a href="https://livetyping.com/ru/blog/predproectnaya-analytica-cho-eto-u-zachem-vam-eto-nugno" target="_blank" rel="noreferrer">Ссылка про анализ</a></p>',25),n=[s];function h(c,d,p,u,_,m){return t(),e("div",null,n)}const q=a(o,[["render",h]]);export{f as __pageData,q as default};
